function select(){const e=document.querySelector(".selected"),n=document.querySelector(".selected span"),t=document.querySelector(".options-container"),c=document.querySelectorAll(".option");e.addEventListener("click",(()=>{t.classList.toggle("active")})),c.forEach((e=>{e.addEventListener("click",(()=>{n.innerHTML=e.querySelector("label").innerHTML,t.classList.remove("active")}))}))}function range(){var e=document.getElementById("myRange"),n=document.querySelector(".range-text__value");n.innerHTML=e.value+"%",e.oninput=function(){n.innerHTML=this.value+"%"}}window.addEventListener("load",(()=>{select(),range()}));